package ch13.service;import ch13.dao.BusDao;import ch13.daomin.Busness;import ch13.daomin.Notice;import ch13.exception.RegisterException;import ch13.utils.MailUtils;import java.sql.SQLException;import java.util.List;public class BusService {    private BusDao dao = new BusDao();    public List<Busness> getAllInfo() {        try {            return dao.getAllInfo();        } catch (SQLException e) {            throw new RuntimeException("查询所有的公告失败！");        }    }    public void deleteBus(String id) {        try {            dao.deleteBus(id);        } catch (SQLException e) {            throw new RuntimeException("根据id删除公告失败！");        }    }}